import 'bootstrap/dist/css/bootstrap.css';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
class App extends React.Component {
  constructor(){
    super();
    this.state={
      isLogin: false,  
      token : localStorage['token']
    }
    }

      async componentWillMount() {
        let res = await fetch( `http://localhost:8000/api/tasks`, {
        method: "GET",
        headers: {
        "Accept": "application/json",
        "Authorization" : `Bearer ${this.state.toke}`,
    }
        })
        .then(res => res.json())
        .then(result => {

           if(result['message'] == "Unauthenticated."){
            this.setState({
              isLogin: false
          })

           }
           else if(result){
            this.setState({
              isLogin: true
          })
           }




        });
        console.log("tasks",this.state.isLogin)

        }


  render(){
    return (
      <div className="App">

   <Route exact path="/">
  {this.state.isLogin ?  <DisplayTodo/>:<Login/> }
</Route>
      </div>
    );
  }

}

export default App;
