import React from "react";

class TodoItem extends React.Component{
    constructor(props){
        super();
        this.state = {
            index : props.index
        }
    }
render(){
    return (
        <div>

  <li class="list-group-item">{this.props.task.name}</li>

<button className="btn btn-danger" onClick={()=>this.props.delete(this.props.task.id)}>
    Delete
</button>
<a type="button" className="btn btn-primary" data-toggle="modal" data-target="#exampleModal">
Edit
</a>




        </div>
    )
}

}
export default TodoItem;
